describe("Ext.Element.traversal",function(){var B,F,G,A,D,C,E,H;beforeEach(function(){B=Ext.getBody().createChild({id:"ExtElementHelper",cls:"wrapper",style:"position:absolute;",children:[{id:"child1",style:"position:absolute;"},{id:"child2",style:"position:absolute;"},{id:"child3",style:"position:absolute;"},{id:"child4",children:[{id:"child4_1",cls:"findIt",children:[{id:"child4_1_1"}]}]}]});F=Ext.getBody().createChild({id:"ExtElementInputHelper",tag:"input",type:"text"});G=Ext.get("child1");A=Ext.get("child2");D=Ext.get("child3");C=Ext.get("child4");E=Ext.get("child4_1");H=Ext.get("child4_1_1")});afterEach(function(){Ext.each([B,F,G,A,D,C,E,H],function(I){I.destroy()})});describe("findParentNode",function(){it("should return document.body",function(){expect(B.findParentNode("body")).toEqual(document.body)});it("should return a dom",function(){expect(G.findParentNode(".wrapper")).toEqual(Ext.getDom(B))});it("should return an el",function(){expect(G.findParentNode(".wrapper",null,true)).toEqual(B)});describe("when maxDepth",function(){describe("1",function(){it("should not return the el",function(){expect(E.findParentNode(".wrapper",1)).toBeNull()})});describe("2",function(){it("should not return the el",function(){expect(E.findParentNode(".wrapper",2)).toEqual(Ext.getDom(B))})})})});describe("up",function(){it("should return Ext.getBody()",function(){expect(B.up("body")).toEqual(Ext.getBody())});it("should return a el",function(){expect(G.up(".wrapper")).toEqual(B)});describe("when maxDepth",function(){describe("1",function(){it("should not return the el",function(){expect(E.up(".wrapper",1)).toBeNull()})});describe("2",function(){it("should not return the el",function(){expect(E.up(".wrapper",2)).toEqual(B)})})})});describe("select",function(){it("should return an Ext.CompositeELementLite",function(){var I=B.select("div");expect(I).toBeDefined();expect(I.elements.length).toEqual(6);expect(I instanceof Ext.CompositeElementLite).toBe(true)})});describe("query",function(){it("should return elements",function(){var I=B.query("div");expect(I).toBeDefined();expect(I.length).toEqual(6);expect(I.isComposite).toBeFalsy();expect(Ext.isArray(I)).toBeTruthy()})});describe("down",function(){it("should return an el",function(){var I=B.down(".findIt");expect(I).toBeDefined();expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=B.down(".findIt",true);expect(I).toBeDefined();expect(Ext.isElement(I)).toBeTruthy()})});describe("child",function(){it("should return null",function(){var I=B.child(".findIt");expect(I).toBeNull()});it("should return an el",function(){var I=C.child(".findIt");expect(I).toBeDefined();expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=C.child(".findIt",true);expect(I).toBeDefined();expect(Ext.isElement(I)).toBeTruthy()})});describe("parent",function(){it("should return an el",function(){var I=G.parent();expect(I).toBeDefined();expect(I).toEqual(B);expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=G.parent(null,true);expect(I).toBeDefined();expect(I).toEqual(Ext.getDom(B));expect(Ext.isElement(I)).toBeTruthy()})});describe("next",function(){it("should return an el",function(){var I=G.next();expect(I).toBeDefined();expect(I).toEqual(A);expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=G.next(null,true);expect(I).toBeDefined();expect(I).toEqual(Ext.getDom(A));expect(Ext.isElement(I)).toBeTruthy()})});describe("prev",function(){it("should return an el",function(){var I=A.prev();expect(I).toBeDefined();expect(I).toEqual(G);expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=A.prev(null,true);expect(I).toBeDefined();expect(I).toEqual(Ext.getDom(G));expect(Ext.isElement(I)).toBeTruthy()})});describe("first",function(){it("should return an el",function(){var I=B.first();expect(I).toBeDefined();expect(I).toEqual(G);expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=B.first(null,true);expect(I).toBeDefined();expect(I).toEqual(Ext.getDom(G));expect(Ext.isElement(I)).toBeTruthy()})});describe("last",function(){it("should return an el",function(){var I=B.last();expect(I).toBeDefined();expect(I).toEqual(C);expect(Ext.isElement(I)).toBeFalsy()});it("should return a dom",function(){var I=B.last(null,true);expect(I).toBeDefined();expect(I).toEqual(Ext.getDom(C));expect(Ext.isElement(I)).toBeTruthy()})});describe("findParent",function(){it("should return document.body",function(){expect(B.findParent("body")).toEqual(document.body)});it("should return a dom",function(){expect(G.findParent(".wrapper")).toEqual(Ext.getDom(B))});it("should return an el",function(){expect(G.findParent(".wrapper",null,true)).toEqual(B)});describe("when maxDepth",function(){describe("1",function(){it("should not return the el",function(){expect(E.findParent(".wrapper",1)).toBeNull()})});describe("2",function(){it("should not return the el",function(){expect(E.findParent(".wrapper",2)).toBeNull()})});describe("3",function(){it("should return the el",function(){expect(E.findParent(".wrapper",3)).toEqual(Ext.getDom(B))})});describe("NaN",function(){it("should use Number.MAX_VALUE",function(){expect(E.findParent(".wrapper",Ext.getBody())).toEqual(Ext.getDom(B))})})})});describe("contains",function(){it("should return false for siblings",function(){expect(Ext.fly(G).contains(H)).toBe(false);expect(Ext.fly(A).contains(H)).toBe(false)});it("should return true for parents",function(){expect(Ext.fly(E).contains(H)).toBe(true)});it("should return true for grandparents",function(){expect(Ext.fly(C).contains(H)).toBe(true)});it("should return true for self",function(){expect(Ext.fly(H).contains(H)).toBe(true)})})},"/src/dom/Element.traversal.js");