describe("Ext.data.identifier.Uuid",function(){var A=Ext.data.identifier.Generator,C;function B(D){C=new Ext.data.identifier.Uuid(D)}afterEach(function(){C=null;A.all={uuid:Ext.data.identifier.Uuid.Global}});describe("defaults",function(){it("should create a global instance with the id 'uuid",function(){expect(Ext.Factory.dataIdentifier("uuid") instanceof Ext.data.identifier.Uuid).toBe(true)})});it("should allow creation of a new instance",function(){B({id:"foo"});expect(Ext.Factory.dataIdentifier("foo")).toBe(C);expect(Ext.Factory.dataIdentifier("uuid")).not.toBe(C)});describe("generate",function(){it("should be able to generate version 1 ids",function(){B({version:1,clockSeq:12579,salt:{hi:57005&~256,lo:195935983},timestamp:{hi:233811181,lo:195948557}});expect(C.generate()).toBe("0badf00d-aced-1def-b123-dfad0badbeef");expect(C.generate()).toBe("0badf00e-aced-1def-b123-dfad0badbeef")});it("should generate Version 4 uuids",function(){B();var E=/[0-9a-f]{8}(\-[0-9a-f]{4}){3}\-[0-9a-f]{12}/;var D=C.generate(),F=C.generate();expect(D).toMatch(E);expect(F).toMatch(E);expect(D.charAt(14)).toEqual("4");expect(F.charAt(14)).toEqual("4")})})});