if(Ext.supports.touchScroll){describe("Ext.scroll.Scroller",function(){it("should fire the callback once after scrolling",function(){var B=0,A=new Ext.panel.Panel({height:100,width:100,frame:true,title:"ScrollTest",html:'<div style="height:500;width:100%">1<br>2<br>4<br>4<br>5<br>6<br>7<br>8<br>9<br>10<br></div>',renderTo:document.body,autoScroll:true});A.scrollTo(0,50,{duration:1,callback:function(){B++}});waitsFor(function(){return B});runs(function(){expect(A.getScrollY()).toBe(50);expect(B).toBe(1);A.destroy()})})})};